mapper.select:
SELECT 

FROM transaction_contract a

WHERE a.id = #{id}

		SELECT 

		FROM transaction_contract a

		WHERE a.del_flag = #{DEL_FLAG_NORMAL}

			AND a.num = #{num}


			AND a.bill_id = #{billId.id}


			AND b.contract_num = #{billId.contractNum}


			AND a.supplier_enterprise_id = #{supplierEnterpriseId.id}


			AND a.supplier_parent_id = #{supplierParentId.id}


			AND a.core_enterprise_id = #{coreEnterpriseId.id}

		AND a.start_time >= #{searchStartDate}
 		AND a.start_time <= #{searchEndDate}
		ORDER BY a.start_time DESC

		SELECT 
			count(1)
		FROM transaction_contract a

		WHERE a.del_flag = #{DEL_FLAG_NORMAL}

			AND a.num = #{num}


			AND a.bill_id = #{billId.id}


			AND b.contract_num = #{billId.contractNum}


			AND a.supplier_enterprise_id = #{supplierEnterpriseId.id}


			AND a.supplier_parent_id = #{supplierParentId.id}


			AND a.core_enterprise_id = #{coreEnterpriseId.id}

		AND a.start_time >= #{searchStartDate}
 		AND a.start_time <= #{searchEndDate}

mapper.update:
UPDATE transaction_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
