mapper.select:
SELECT
	a.id, a.name, a.login_name
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id = #{office.id}
ORDER BY a.name

mapper.update:
UPDATE sys_user SET 
	password = #{password} 
WHERE id = #{id}

UPDATE sys_user SET 
	login_ip = #{loginIp}, 
	login_Date = #{loginDate} 
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.insert:
INSERT INTO sys_user_friend(
	id, 
	userId, 
	friendId
) VALUES (
	#{id}, 
	#{userId}, 
	#{friendId}
)

mapper.select:
SELECT
FROM sys_user a
LEFT JOIN sys_user_friend p ON p.friendId = a.id WHERE p.userId = #{id}
